import React from "react";
import { useRef, useState, useEffect,useContext } from "react";
import AuthContext from "../context/AuthProivder";
function Login() {
  const userRef = useRef();
  const errRef = useRef();
  const [user, setUser] = useState("");
  const [pwd, setPwd] = useState("");
  const [errMsg, setErrMsg] = useState("");
  const [success, setSuccess] = useState(false);
  useEffect(() => {
    userRef.current.focus();
  }, []);
  useEffect(() => {
    setErrMsg("");
  }, [user, pwd]);
  
  const handle = async (e) =>{
    e.preventDefault()
    setUser('')
    setPwd('')
    setSuccess(true)
  }

  return (
    <>
    {!success ? 
    (<form action="" onSubmit={handle}>
        <label htmlFor="username">Username</label>
        <input type="text" name="username" id="username" ref={userRef} value={user} onChange={(e) => setUser(e.target.value)} required/>
        <label htmlFor="password">Password</label>
        <input type="password" name="password" id="password" value={pwd} onChange={(e) => setPwd(e.target.value)} required />
        <input type="submit" value="Login" />
      </form>) 
      :
    (<h1>You are logged in!</h1>)
    }
      
    </>
  );
}

export default Login;
